"use strict";var worksWebpages=document.querySelector(".works__webpages"),worksList=document.querySelector(".works__list"),skillsBlockWraps=document.querySelectorAll(".skills__block-wrap");worksWebpages.addEventListener("click",function(){worksWebpages.classList.contains("works__block_clicked")?(worksList.style.display="none",worksWebpages.classList.remove("works__block_clicked")):(worksList.style.display="block",worksWebpages.classList.add("works__block_clicked"))}),skillsBlockWraps.forEach(function(s){var l=s.querySelector(".skills__block"),e=s.querySelector(".skills__list");l.addEventListener("click",function(){l.classList.contains("skills__block_clicked")?(e.style.display="none",l.classList.remove("skills__block_clicked")):(e.style.display="block",l.classList.add("skills__block_clicked"))})});